sequence.script:


			var newerThan = mc.getProperty('uri.var.newerThan');

			var added = 0;
            var query = "";

			if (newerThan != null && newerThan != "") {
				if(Boolean(added)) {
			        query = query + '&amp;newerThan='+newerThan;
			    }else {
			        query = query + 'newerThan='+newerThan;
			        added = 1;
			    }
			}

			mc.setProperty('uri.var.params', query);
